sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.status_code == '200') {
    var payload = JSON.parse(inputs.payload);
    outputs.number_of_records = payload.total;
    var start = parseInt(Number(inputs.startAt));
    var maximum = parseInt(Number(inputs.max));
    if(payload.total > start+maximum)
    	outputs.end = start+maximum;
    else
      	outputs.end = -1;
    outputs.issues = payload;
    outputs.status = "Success";
  } else {
    outputs.status = "Error";
    outputs.error_message = new JiraErrorHandler().setError(inputs.status_code, inputs.payload);
  }
})(inputs, outputs);

