sys_variable_value.value:
(function execute(inputs, outputs) {
  if (inputs.jql_query.trim() == "")
    throw new Error("JQL Query cannot be empty ");
  outputs.api_version = new JiraUtils().getAPIVersion(inputs.sys_alias.sys_id);
  if(inputs.max > 50)
    inputs.max = 50;
  outputs.startat = inputs.startat.toString();
  outputs.max_results = inputs.max.toString();

})(inputs, outputs);

